import React, { useState, useContext } from 'react'
import { Button, Grow, Container, Drawer, List, ListItem, Collapse, Modal } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles'
import Link from 'next/link'
import Router from 'next/router'
import dynamic from 'next/dynamic'
import localStorage from 'localStorage'
import { THEME_NAME, SUPPORTED_THEMES } from 'consts'

const BackTop = dynamic(import('components/BackTop'), { ssr: false })

const Nav = () => {
  const COMMON_MENU = [
    ['/downloads', '下载客户端'],
    ['/billing', '充值'],
    ['/game_list', '支持游戏'],
    ['/article', '资讯百科'],
    ['/router', '路由器插件']
  ]
  const [visible, setVisible] = useState(false)
  const [show, setShow] = useState(false)
  const useStyles = makeStyles(theme => ({
    paper: {
      outline: 0,
      padding: 20,
      width: 400,
      maxWidth: '100%',
      color: theme.palette.text.primary,
      background: theme.palette.background.paper
    },
    navbar: {
      boxShadow: '0 4px 8px 0 rgba(0, 0, 0, 0.06)',
      background: theme.palette.background.paper
    }
  }))
  const classes = useStyles()
  const ThemeMenuHorizontal = () => {
    return (
      <li className="dropdown mega-menu">
        <a
          role="presentation"
          onClick={() => {
            localStorage.setItem('THEME', localStorage.getItem('THEME') === 'light' ? 'dark' : 'light')
            window.location.reload()
          }}
        >
          切换主题
        </a>
        <style jsx>
          {`
            .no-bullet {
              list-style-type: none;
            }
          `}
        </style>
      </li>
    )
  }
  const rightIconMenuNotLoggedIn = () => {
    return (
      <List>
        <span role="presentation">
          <ListItem button onClick={() => setVisible(false)}>
            登录
          </ListItem>
        </span>
        {[['/signup', '免费注册'], ...COMMON_MENU].map(v => (
          <Link href={v[0]} key={v[0]}>
            <ListItem button onClick={() => setVisible(false)}>
              <span>{v[1]}</span>
            </ListItem>
          </Link>
        ))}
        <ListItem button onClick={() => setShow(!show)}>
          {THEME_NAME[localStorage.getItem('THEME') || 'dark']}
          {show ? <ExpandLess /> : <ExpandMore />}
        </ListItem>
        <Collapse in={show} timeout="auto" unmountOnExit>
          <List>
            {SUPPORTED_THEMES.map(v => (
              <ListItem
                key={v}
                button
                onClick={() => {
                  localStorage.setItem('THEME', v)
                  window.location.reload()
                }}
              >
                {THEME_NAME[v]}
              </ListItem>
            ))}
          </List>
        </Collapse>
      </List>
    )
  }

  const rightIconMenuLoggedIn = () => {
    return (
      <List>
        {[...COMMON_MENU, ['/my/account', '会员中心']].map(v => (
          <Link href={v[0]} key={v[0]}>
            <ListItem button onClick={() => setVisible(false)}>
              <span>{v[1]}</span>
            </ListItem>
          </Link>
        ))}
        <ListItem button onClick={() => setShow(!show)}>
          {THEME_NAME[localStorage.getItem('THEME') || 'dark']}
        </ListItem>
        <Collapse in={show} timeout="auto" unmountOnExit>
          <List>
            {SUPPORTED_THEMES.map(v => (
              <ListItem
                key={v}
                button
                onClick={() => {
                  localStorage.setItem('THEME', v)
                  window.location.reload()
                }}
              >
                {THEME_NAME[v]}
              </ListItem>
            ))}
          </List>
        </Collapse>
        <ListItem
          button
          onClick={() => {
            setVisible(false)
          }}
        >
          登出
        </ListItem>
      </List>
    )
  }

  const horizontalMenuNotLoggedIn = () => {
    return (
      <ul className="nav navbar-nav">
        {COMMON_MENU.map(v => (
          <li className="mega-menu" key={v} id={v[1]}>
            <Link href={v[0]}>
              <a>{v[1]}</a>
            </Link>
          </li>
        ))}
        {ThemeMenuHorizontal()}
        <li className="mega-menu" id="登录">
          <a className="C-P">
            <span role="presentation">登录</span>
          </a>
        </li>
        <li className="mega-menu" id="免费注册">
          <a>
            <Button disableElevation color="primary" variant="contained" onClick={() => Router.push('/signup')}>
              免费注册
            </Button>
          </a>
        </li>
      </ul>
    )
  }

  const horizontalMenuLoggedIn = () => {
    return (
      <ul className="nav navbar-nav">
        {[...COMMON_MENU].map(v => (
          <li className="mega-menu" key={v}>
            <Link href={v[0]}>
              <a className="C-P">{v[1]}</a>
            </Link>
          </li>
        ))}
        <li className="mega-menu">
          <Link href="/my/account">
            <a target="_blank" rel="noreferrer" className="C-P">
              会员中心
            </a>
          </Link>
        </li>
        {ThemeMenuHorizontal()}
        <li className="mega-menu">
          <a>
            <div role="presentation" className="C-P">
              登出
            </div>
          </a>
        </li>
      </ul>
    )
  }
  return (
    <>
      <nav className={`main-nav ${classes.navbar}`}>
        <div className="container">
          <div className="brand-logo">
            <span role="presentation" onClick={() => Router.push('/')}>
              <img src="/imgs/lingti-logo.png" alt="" style={{ height: 40, marginTop: -10 }} />
            </span>
          </div>
          <div className="navbar-header">
            <div className="inner-nav">
              <ul>
                <li className="navbar-toggle">
                  <a style={{ textDecoration: 'none' }}>
                    <div role="presentation" onClick={() => setVisible(true)} style={{ fontSize: 20, color: 'currentColor' }} />
                  </a>
                </li>
              </ul>
            </div>
          </div>
          <Drawer anchor="right" open={visible} onClose={() => setVisible(false)}>
