import React, { useEffect } from 'react'
import dynamic from 'next/dynamic'

const QuillNoSSRWrapper = dynamic(import('react-quill'), {
  ssr: false,
  loading: () => <p>Loading ...</p>
})
const IndexPage = () => {
  const setTawk = () => {
    const s1 = document.createElement('script')
    const s0 = document.getElementsByTagName('head')[0]
    s1.async = true
    s1.src = 'https://client.crisp.chat/l.js'
    s1.charset = 'UTF-8'
    s1.setAttribute('crossorigin', '*')
    s0.parentNode.insertBefore(s1, s0)
  }
  useEffect(() => {
    setTawk()
  }, [])
  const formats = ['header', 'font', 'size', 'bold', 'italic', 'underline', 'strike', 'blockquote', 'list', 'bullet', 'indent', 'link', 'image', 'video']
  const modules = {
    toolbar: [
      [{ header: '1' }, { header: '2' }, { font: [] }],
      [{ size: [] }],
      ['bold', 'italic', 'underline', 'strike', 'blockquote'],
      [{ list: 'ordered' }, { list: 'bullet' }, { indent: '-1' }, { indent: '+1' }],
      ['link', 'image', 'video'],
      ['clean']
    ]
  }
  return <QuillNoSSRWrapper modules={modules} formats={formats} theme="snow" />
}

export default IndexPage
