import React, { useRef, useState, useEffect } from 'react'
import dynamic from 'next/dynamic'

const QuillNoSSRWrapper = dynamic(import('react-quill'), {
  ssr: false,
  loading: () => <p>Loading ...</p>
})
const Region = 'https://res.lingti666.com/img/article' /* 存储桶所在地域，必须字段 */

const IndexPage = () => {
  const cos = new COS({
    getAuthorization(options, callback) {
      // 异步获取临时密钥
      $.get(
        'http://example.com/server/sts.php',
        {
          bucket: options.Bucket,
          region: options.Region
        },
        function(data) {
          const credentials = data && data.credentials
          if (!data || !credentials) return console.error('credentials invalid')
          callback({
            TmpSecretId: credentials.tmpSecretId,
            TmpSecretKey: credentials.tmpSecretKey,
            XCosSecurityToken: credentials.sessionToken,
            // 建议返回服务器时间作为签名的开始时间，避免用户浏览器本地时间偏差过大导致签名错误
            StartTime: data.startTime, // 时间戳，单位秒，如：1580000000
            ExpiredTime: data.expiredTime // 时间戳，单位秒，如：1580000900
          })
        }
      )
    }
  })

  const formats = ['header', 'font', 'size', 'bold', 'italic', 'underline', 'strike', 'blockquote', 'list', 'bullet', 'indent', 'link', 'image', 'video']
  const modules = {
    toolbar: [
      [{ header: '1' }, { header: '2' }, { font: [] }],
      [{ size: [] }],
      ['bold', 'italic', 'underline', 'strike', 'blockquote'],
      [{ list: 'ordered' }, { list: 'bullet' }, { indent: '-1' }, { indent: '+1' }],
      ['link', 'image', 'video'],
      ['clean']
    ],
    clipboard: {
      // toggle to add extra line breaks when pasting HTML:
      matchVisual: false
    }
  }
  return <QuillNoSSRWrapper modules={modules} formats={formats} theme="snow" />
}

export default IndexPage
