import React from 'react'
import App from 'next/app'
import Router from 'next/router'
import '../styles/main.scss'

class MyApp extends App {
  static async getInitialProps({ Component, ctx }) {
    if (typeof window === 'undefined') {
      global.navigator = {}
      global.navigator.languages = {}
      global.location = {}
    }
    const pageProps = Component.getInitialProps ? await Component.getInitialProps(ctx) : {}
    return { pageProps }
  }

  componentDidMount() {
    const UA = navigator.userAgent.toLowerCase()
    const ieV = UA.indexOf('msie') !== -1 ? parseInt(UA.split('msie')[1], 10) : -1
    if (ieV !== -1 && ieV < 10) {
      window.location.href = 'https://support.dmeng.net/upgrade-your-browser.html'
    }
    if (global.location.pathname.search('/my') > -1) {
      if (!localStorage.getItem('Token')) {
        Router.push('/')
      }
    }
  }

  render() {
    const { Component, pageProps, err, store } = this.props
    const modifiedPageProps = { ...pageProps, err }
    return <Component {...modifiedPageProps} />
  }
}

export default MyApp
